import React, { useState } from "react";
import TodoItem from "./components/todo-item/TodoItem";

const TODOS_MOCK = [
  {
    id: "1",
    title: "Todo 1",
    description: "1Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "2",
    title: "Todo 2",
    description: "2Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "3",
    title: "Todo 3",
    description: "3Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
  {
    id: "4",
    title: "Todo 4",
    description: "4Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
];

function App() {
  const [todos, setTodos] = useState(TODOS_MOCK);

  const handleToggleTodo = (id, isChecked) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, completed: isChecked } : todo
      )
    );
  };

  return (
    <div className="App">
      <div className="app-container">
        <h1>My Todos</h1>
        <div className="todo-list">
          {todos.map((todo) => (
            <TodoItem key={todo.id} todo={todo} onToggle={handleToggleTodo} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
