import React from "react";
import "./TodoItem.css";
import Checkbox from "../checkbox/Checkbox"; // Asigură-te că importul este corect

const TodoItem = ({ todo, onCheckboxChange }) => {
  const handleCheckboxChange = () => {
    onCheckboxChange(todo.id); // Apelăm funcția de actualizare a stării cu id-ul activității
  };

  return (
    <div className={`todo-item ${todo.completed && "todo-completed"}`}>
      <div className="todo-item-header">
        <div className="title-area">
          <Checkbox checked={todo.completed} onChange={handleCheckboxChange} />{" "}
          {/* Utilizăm componenta Checkbox */}
          <h4>{todo.title}</h4>
        </div>
        <div>
          <i className="fa fa-pencil" aria-hidden="true"></i>
          <i className="fa fa-trash" aria-hidden="true"></i>
        </div>
      </div>

      <div className="separator"></div>

      <p>{todo.description}</p>
    </div>
  );
};

export default TodoItem;
