import React from "react";
import "./TodoItem.css";
import Checkbox from "../checkbox/CheckBox";

const TodoItem = (props) => {
  const [checked, setChecked] = useState(false);

  const handleCheckboxChange = () => {
    setChecked(!checked); // Invert the checked state
    if (!checked) {
      // If the item is being checked
      setTimeout(() => {
        props.onMoveToCompleted(props.todo.id); // Move the item to completed after a delay
      }, 3000); // Adjust the delay as needed
    }
  };

  const handleDelete = () => {
    props.onDelete(props.todo.id);
  };

  const handleEdit = () => {
    props.onEdit();
  };

  return (
    <div className={`todo-item ${props.completed && "todo-completed"}`}>
      <div className="todo-item-header">
        <div className="title-area">
          <Checkbox
            checked={checked}
            onChange={handleCheckboxChange}
            greenCheck // Add a prop to indicate green check
          />
          <h4>{props.todo.title}</h4>
        </div>
        <div>
          <i
            className="fa fa-pencil"
            aria-hidden="true"
            onClick={handleEdit}
          ></i>{" "}
          <i
            className="fa fa-trash"
            aria-hidden="true"
            onClick={handleDelete}
          ></i>
        </div>
      </div>
      <div className="separator"></div>
      <p>{props.todo.description}</p>
    </div>
  );
};

export default TodoItem;
