import React, { useState } from "react";
import Card from "./components/card/Card";
import TodoItem from "./components/todo-item/TodoItem";
import Input from "./components/input/Input";
import TextArea from "./components/input/TextArea";
import Button from "./components/button/Button";
import Modal from "./components/modal/Modal";
import "./App.css";

const TODOS_MOCK = [
  {
    id: "1",
    title: "Todo 1",
    description: "1Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "2",
    title: "Todo 2",
    description: "2Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "3",
    title: "Todo 3",
    description: "3Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
  {
    id: "4",
    title: "Todo 4",
    description: "4Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
];

function App() {
  const [todos, setTodos] = useState(TODOS_MOCK);

  const handleCheckboxChange = (id, isChecked) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, completed: isChecked } : todo
    );
    setTodos(updatedTodos);
  };

  return (
    <div className="App">
      <div className="app-container">
        <Card>
          <h2>Create Todo</h2>
          {/* Codul pentru adăugarea de noi todo-uri */}
        </Card>

        <Card>
          <h1>My todos</h1>
          <div className="list-container">
            {todos
              .filter((todo) => !todo.completed)
              .map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onCheckboxChange={handleCheckboxChange}
                />
              ))}
          </div>

          <div className="separator"></div>

          <h2>Completed</h2>
          <div className="list-container">
            {todos
              .filter((todo) => todo.completed)
              .map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onCheckboxChange={handleCheckboxChange}
                />
              ))}
          </div>
        </Card>
      </div>
      {/* Modal pentru formularul de adăugare a activităților */}
      {/* Codul pentru modal */}
    </div>
  );
}

export default App;
