import React from "react";
import "./TodoItem.css";
import Checkbox from "../checkbox/CheckBox";

const TodoItem = (props) => {
  const handleCheckboxChange = (isChecked) => {
    props.onCheckboxChange(props.todo.id, isChecked);
  };

  const handleDelete = () => {
    props.onDelete(props.todo.id);
  };

  const handleEdit = () => {
    props.onEdit();
  };

  const handleMoveToCompleted = () => {
    props.onMoveToCompleted(props.todo.id); // Transmitem ID-ul elementului
  };

  return (
    <div className={`todo-item ${props.completed && "todo-completed"}`}>
      <div className="todo-item-header">
        <div className="title-area">
          {props.markedForCompletion ? (
            <Checkbox checked={true} onChange={handleMoveToCompleted} />
          ) : (
            <Checkbox
              checked={!!props.completed}
              onChange={handleCheckboxChange}
            />
          )}
          <h4>{props.todo.title}</h4>
        </div>
        <div>
          <i
            className="fa fa-pencil"
            aria-hidden="true"
            onClick={handleEdit}
          ></i>{" "}
          <i
            className="fa fa-trash"
            aria-hidden="true"
            onClick={handleDelete}
          ></i>
        </div>
      </div>
      <div className="separator"></div>
      <p>{props.todo.description}</p>
    </div>
  );
};

export default TodoItem;
