import React, { useState } from "react";
import TodoItem from "./components/todo-item/TodoItem";
import Modal from "./components/modal/Modal"; // Importăm componenta Modal
import Button from "./components/button/Button";

const TODOS_MOCK = [
  {
    id: "1",
    title: "Todo 1",
    description: "1Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "2",
    title: "Todo 2",
    description: "2Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: false,
  },
  {
    id: "3",
    title: "Todo 3",
    description: "3Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
  {
    id: "4",
    title: "Todo 4",
    description: "4Lorem ipsum dolor sit amet consectetur adipisicing elit.",
    completed: true,
  },
];

function App() {
  const [todos, setTodos] = useState(TODOS_MOCK);

  const handleToggleTodo = (id, isChecked) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, completed: isChecked } : todo
      )
    );
  };

  return (
    <div className="App">
      <div className="app-container">
        {/* Card-ul pentru adăugarea de noi activități */}
        <Card>
          <h2>Create Todo</h2>
          <Button onClick={() => setIsModalOpen(true)}>Add +</Button>{" "}
          {/* Deschidem modalul la click pe buton */}
        </Card>

        {/* Card-ul care conține lista de activități */}
        <Card>
          <h1>My todos</h1>
          <div className="list-container">
            {todos
              .filter((todo) => !todo.completed)
              .map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onChange={handleToggleTodo}
                />
              ))}
          </div>

          <div className="separator"></div>

          <h2>Completed</h2>
          <div className="list-container">
            {todos
              .filter((todo) => todo.completed)
              .map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onChange={handleToggleTodo}
                />
              ))}
          </div>
        </Card>
      </div>
      {/* Modal pentru formularul de adăugare a activităților */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2>Create Todo</h2>
        <form onSubmit={handleSubmit}>
          <Input
            value={formData.title}
            onChange={(e) =>
              setFormData({ ...formData, title: e.target.value })
            }
            placeholder="Title"
            type="text"
          />
          <TextArea
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            placeholder="Description"
          />
          <Button type="submit">Create</Button>
        </form>
      </Modal>
    </div>
  );
}

export default App;
